// For Judge0-like execution, read from stdin and print to stdout.
// Input format: 
// Line 1: JSON array of numbers (e.g., [2,7,11,15])
// Line 2: Target integer (e.g., 9)
// Output format: JSON array of two indices (e.g., [0,1])

function twoSum(nums, target) {
    const numMap = new Map();
    for (let i = 0; i < nums.length; i++) {
        const complement = target - nums[i];
        if (numMap.has(complement)) {
            return [numMap.get(complement), i].sort((a,b) => a - b);
        }
        numMap.set(nums[i], i);
    }
    return []; // Should not be reached
}

