import java.util.Scanner;
import java.util.ArrayList;
import java.util.List;

public class Main { // Class name MUST be Main
    public static List<Long> calculatePrefixSum(List<Integer> arr) {
        List<Long> prefixSums = new ArrayList<>();
        if (arr == null) { // Check for null explicitly
            return prefixSums;
        }
        if (arr.isEmpty()) {
             return prefixSums;
        }
        prefixSums.add((long)arr.get(0));
        for (int i = 1; i < arr.size(); i++) {
            prefixSums.add(prefixSums.get(i - 1) + arr.get(i));
        }
        return prefixSums;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String line = "";
        if (sc.hasNextLine()) { // Check if there is input to prevent NoSuchElementException
             line = sc.nextLine();
        }
        sc.close();

        line = line.trim();
        List<Integer> arr = new ArrayList<>();
        if (line.startsWith("[") && line.endsWith("]") && line.length() >= 2) {
            String content = line.substring(1, line.length() - 1).trim();
            if (!content.isEmpty()) {
                String[] numbersStr = content.split(",");
                for (String numStr : numbersStr) {
                    String trimmedNumStr = numStr.trim();
                    if (!trimmedNumStr.isEmpty()) {
                        try {
                            arr.add(Integer.parseInt(trimmedNumStr));
                        } catch (NumberFormatException e) {
                            // System.err.println("Skipping invalid number: " + trimmedNumStr);
                        }
                    }
                }
            }
        }

        List<Long> result = calculatePrefixSum(arr);

        System.out.print("[");
        for (int i = 0; i < result.size(); i++) {
            System.out.print(result.get(i));
            if (i < result.size() - 1) {
                System.out.print(",");
            }
        }
        System.out.println("]");
    }
}

// Input format: Single line JSON array of characters (e.g., ["h","e","l","l","o"])
// Output format: Single line JSON array (e.g., ["o","l","l","e","h"])

function reverseString(s) {
    let left = 0;
    let right = s.length - 1;
    while (left < right) {
        [s[left], s[right]] = [s[right], s[left]];
        left++;
        right--;
    }
}

